import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertUserSchema, insertCoachSchema, insertStudentSchema, insertBookingSchema, insertReviewSchema } from "@shared/schema";
import { fetchGoogleReviewsData, isValidGoogleReviewsUrl } from "@shared/googleReviews";
import bcrypt from "bcrypt";
import session from "express-session";
import {
  ObjectStorageService,
  ObjectNotFoundError,
} from "./objectStorage.js";
import { ObjectPermission } from "./objectAcl.js";

export async function registerRoutes(app: Express): Promise<Server> {
  // Session middleware for authentication
  app.use(session({
    secret: process.env.SESSION_SECRET || 'fallback-secret-change-in-production',
    resave: false,
    saveUninitialized: false,
    cookie: { 
      secure: process.env.NODE_ENV === 'production',
      httpOnly: true,
      sameSite: 'lax',
      maxAge: 24 * 60 * 60 * 1000, // 24 hours
    },
  }));

  // Authentication Routes
  app.post("/api/auth/register", async (req, res) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ error: "Email and password are required" });
      }

      const existingUser = await storage.getUserByEmail(email);
      if (existingUser) {
        return res.status(400).json({ error: "User already exists with this email" });
      }

      // SECURITY: Always create new users as 'student' role only
      // Admin users must be created manually by existing admins
      const userData = { email, password, role: "student" };
      const user = await storage.createUser(userData);
      (req.session as any).userId = user.id;

      res.json({ user: { id: user.id, email: user.email, role: user.role } });
    } catch (error) {
      console.error("Registration error:", error);
      res.status(400).json({ error: "Registration failed" });
    }
  });

  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ error: "Email and password are required" });
      }

      const user = await storage.verifyPassword(email, password);
      if (!user) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      // Regenerate session to prevent session fixation attacks
      req.session.regenerate((err) => {
        if (err) {
          console.error("Session regeneration failed:", err);
        }
        (req.session as any).userId = user.id;
        res.json({ user: { id: user.id, email: user.email, role: user.role } });
      });
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ error: "Login failed" });
    }
  });

  app.post("/api/auth/logout", (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ error: "Logout failed" });
      }
      res.clearCookie('connect.sid');
      res.json({ message: "Logged out successfully" });
    });
  });

  // Check authentication status
  app.get("/api/auth/me", async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      if (!userId) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(401).json({ error: "User not found" });
      }

      res.json({ user: { id: user.id, email: user.email, role: user.role } });
    } catch (error) {
      console.error("Auth check error:", error);
      res.status(500).json({ error: "Authentication check failed" });
    }
  });

  // Coach Registration and Profile Routes
  app.post("/api/coaches/register", async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      if (!userId) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const user = await storage.getUser(userId);
      if (user?.role !== 'coach') {
        return res.status(403).json({ error: "Only users with coach role can create coach profiles" });
      }

      const coachData = insertCoachSchema.parse({ ...req.body, userId });
      const coach = await storage.createCoach(coachData);

      // Handle specialties, tools, certifications, videos
      const { specialties = [], tools = [], certifications = [], videos = [] } = req.body;

      // Persist specialties
      for (const specialty of specialties) {
        await storage.createCoachSpecialty(coach.id, specialty);
      }

      // Persist tools
      for (const tool of tools) {
        await storage.createCoachTool(coach.id, tool);
      }

      // Persist certifications
      for (const certification of certifications) {
        await storage.createCoachCertification(coach.id, certification);
      }

      // Persist videos
      for (const video of videos) {
        await storage.createCoachVideo(coach.id, video);
      }

      res.json({
        message: "Coach registration successful! Your profile is pending admin approval. You will be notified once approved and your profile will become visible to students.",
        coach,
        specialties,
        tools, 
        certifications,
        videos,
        status: "pending_approval"
      });
    } catch (error) {
      console.error("Coach registration error:", error);
      res.status(400).json({ error: "Coach registration failed" });
    }
  });

  app.get("/api/coaches/:id", async (req, res) => {
    try {
      const coach = await storage.getCoachWithDetails(req.params.id);
      if (!coach) {
        return res.status(404).json({ error: "Coach not found" });
      }
      res.json(coach);
    } catch (error) {
      console.error("Get coach error:", error);
      res.status(500).json({ error: "Failed to fetch coach" });
    }
  });

  app.put("/api/coaches/:id", async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      if (!userId) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const coach = await storage.getCoach(req.params.id);
      if (!coach || coach.userId !== userId) {
        return res.status(403).json({ error: "Not authorized to update this coach profile" });
      }

      const updates = insertCoachSchema.partial().parse(req.body);
      const updatedCoach = await storage.updateCoach(req.params.id, updates);

      res.json(updatedCoach);
    } catch (error) {
      console.error("Update coach error:", error);
      res.status(400).json({ error: "Failed to update coach profile" });
    }
  });

  // Coach Search Routes
  app.get("/api/coaches/search", async (req, res) => {
    try {
      const { location, specialties, minPrice, maxPrice, rating, tools } = req.query;

      const searchParams: any = {};

      if (location) searchParams.location = location as string;
      if (minPrice && maxPrice) {
        searchParams.priceRange = { 
          min: parseFloat(minPrice as string), 
          max: parseFloat(maxPrice as string) 
        };
      }
      if (rating) searchParams.rating = parseFloat(rating as string);
      if (specialties) {
        searchParams.specialties = Array.isArray(specialties) 
          ? specialties as string[] 
          : [specialties as string];
      }
      if (tools) {
        searchParams.tools = Array.isArray(tools) 
          ? tools as string[] 
          : [tools as string];
      }

      const coaches = await storage.searchCoaches(searchParams);
      res.json(coaches);
    } catch (error) {
      console.error("Coach search error:", error);
      res.status(500).json({ error: "Coach search failed" });
    }
  });

  // Google Reviews Routes
  app.post("/api/coaches/fetch-google-reviews", async (req, res) => {
    try {
      const { googleReviewsUrl } = req.body;

      if (!googleReviewsUrl) {
        return res.status(400).json({ error: "Google Reviews URL is required" });
      }

      if (!isValidGoogleReviewsUrl(googleReviewsUrl)) {
        return res.status(400).json({ error: "Invalid Google Reviews URL format" });
      }

      const reviewsData = await fetchGoogleReviewsData(googleReviewsUrl);

      if (!reviewsData.isValid) {
        return res.status(400).json({ error: reviewsData.error || "Failed to fetch Google Reviews data" });
      }

      res.json({
        rating: reviewsData.rating,
        reviewCount: reviewsData.reviewCount,
        businessName: reviewsData.businessName,
        isValid: reviewsData.isValid
      });
    } catch (error) {
      console.error("Google Reviews fetch error:", error);
      res.status(500).json({ error: "Failed to fetch Google Reviews data" });
    }
  });

  app.post("/api/coaches/:id/sync-google-reviews", async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      if (!userId) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const coach = await storage.getCoach(req.params.id);
      if (!coach) {
        return res.status(404).json({ error: "Coach not found" });
      }

      // Check if user owns this coach profile or is admin
      const user = await storage.getUser(userId);
      if (coach.userId !== userId && user?.role !== 'admin') {
        return res.status(403).json({ error: "Unauthorized to update this coach profile" });
      }

      if (!coach.googleReviewsUrl) {
        return res.status(400).json({ error: "No Google Reviews URL set for this coach" });
      }

      const reviewsData = await fetchGoogleReviewsData(coach.googleReviewsUrl);

      if (!reviewsData.isValid) {
        return res.status(400).json({ error: reviewsData.error || "Failed to fetch Google Reviews data" });
      }

      // Update coach with Google Reviews data
      await storage.updateCoachGoogleReviews(req.params.id, {
        googleRating: reviewsData.rating,
        googleReviewCount: reviewsData.reviewCount,
        lastGoogleSync: new Date()
      });

      res.json({
        message: "Google Reviews data synced successfully",
        rating: reviewsData.rating,
        reviewCount: reviewsData.reviewCount,
        lastSynced: new Date()
      });
    } catch (error) {
      console.error("Google Reviews sync error:", error);
      res.status(500).json({ error: "Failed to sync Google Reviews data" });
    }
  });

  // Student Profile Routes  
  app.post("/api/students/register", async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      if (!userId) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const studentData = insertStudentSchema.parse({ ...req.body, userId });
      const student = await storage.createStudent(studentData);

      res.json(student);
    } catch (error) {
      console.error("Student registration error:", error);
      res.status(400).json({ error: "Student registration failed" });
    }
  });

  app.get("/api/students/me", async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      if (!userId) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const student = await storage.getStudentByUserId(userId);
      res.json(student);
    } catch (error) {
      console.error("Get student error:", error);
      res.status(500).json({ error: "Failed to fetch student profile" });
    }
  });

  // Booking Routes
  app.post("/api/bookings", async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      if (!userId) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const student = await storage.getStudentByUserId(userId);
      if (!student) {
        return res.status(400).json({ error: "Student profile required for booking" });
      }

      const bookingData = insertBookingSchema.parse({ 
        ...req.body, 
        studentId: student.id 
      });

      const booking = await storage.createBooking(bookingData);
      res.json(booking);
    } catch (error) {
      console.error("Booking creation error:", error);
      res.status(400).json({ error: "Failed to create booking" });
    }
  });

  app.get("/api/bookings/student", async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      if (!userId) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const student = await storage.getStudentByUserId(userId);
      if (!student) {
        return res.json([]);
      }

      const bookings = await storage.getBookingsByStudent(student.id);
      res.json(bookings);
    } catch (error) {
      console.error("Get student bookings error:", error);
      res.status(500).json({ error: "Failed to fetch bookings" });
    }
  });

  app.get("/api/bookings/coach", async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      if (!userId) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const user = await storage.getUser(userId);
      if (user?.role !== 'coach') {
        return res.status(403).json({ error: "Only coaches can access coach bookings" });
      }

      const coach = await storage.getCoach(userId);
      if (!coach) {
        return res.json([]);
      }

      const bookings = await storage.getBookingsByCoach(coach.id);
      res.json(bookings);
    } catch (error) {
      console.error("Get coach bookings error:", error);
      res.status(500).json({ error: "Failed to fetch bookings" });
    }
  });

  // Review Routes
  app.post("/api/reviews", async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      if (!userId) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const student = await storage.getStudentByUserId(userId);
      if (!student) {
        return res.status(400).json({ error: "Student profile required for reviews" });
      }

      const reviewData = insertReviewSchema.parse({
        ...req.body,
        studentId: student.id
      });

      const review = await storage.createReview(reviewData);
      res.json(review);
    } catch (error) {
      console.error("Review creation error:", error);
      res.status(400).json({ error: "Failed to create review" });
    }
  });

  app.get("/api/reviews/coach/:coachId", async (req, res) => {
    try {
      const reviews = await storage.getReviewsByCoach(req.params.coachId);
      res.json(reviews);
    } catch (error) {
      console.error("Get coach reviews error:", error);
      res.status(500).json({ error: "Failed to fetch reviews" });
    }
  });

  // Object Storage Routes

  // Serve public assets
  app.get("/public-objects/:filePath(*)", async (req, res) => {
    const filePath = req.params.filePath;
    const objectStorageService = new ObjectStorageService();
    try {
      const file = await objectStorageService.searchPublicObject(filePath);
      if (!file) {
        return res.status(404).json({ error: "File not found" });
      }
      objectStorageService.downloadObject(file, res);
    } catch (error) {
      console.error("Error searching for public object:", error);
      return res.status(500).json({ error: "Internal server error" });
    }
  });

  // Authentication middleware helper
  const isAuthenticated = (req: any, res: any, next: any) => {
    const userId = (req.session as any)?.userId;
    if (!userId) {
      return res.status(401).json({ error: "Not authenticated" });
    }
    req.user = { id: userId };
    next();
  };

  // Admin middleware helper
  const isAdmin = async (req: any, res: any, next: any) => {
    const userId = (req.session as any)?.userId;
    if (!userId) {
      return res.status(401).json({ error: "Not authenticated" });
    }

    const isAdminUser = await storage.isUserAdmin(userId);
    if (!isAdminUser) {
      return res.status(403).json({ error: "Admin access required" });
    }

    req.user = { id: userId };
    next();
  };

  // The endpoint for serving objects with proper public/private access control
  app.get("/objects/:objectPath(*)", async (req, res) => {
    // Get userId from session if available (but don't require authentication)
    const userId = (req.session as any)?.userId;
    const objectStorageService = new ObjectStorageService();
    try {
      const objectFile = await objectStorageService.getObjectEntityFile(
        req.path,
      );
      const canAccess = await objectStorageService.canAccessObjectEntity({
        objectFile,
        userId: userId,
        requestedPermission: ObjectPermission.READ,
      });
      if (!canAccess) {
        return res.sendStatus(404); // Return 404 instead of 401 for better UX
      }
      objectStorageService.downloadObject(objectFile, res);
    } catch (error) {
      console.error("Error checking object access:", error);
      if (error instanceof ObjectNotFoundError) {
        return res.sendStatus(404);
      }
      return res.sendStatus(500);
    }
  });

  // The endpoint for getting the upload URL for an object entity
  app.post("/api/objects/upload", isAuthenticated, async (req, res) => {
    const objectStorageService = new ObjectStorageService();
    try {
      const uploadURL = await objectStorageService.getObjectEntityUploadURL();
      res.json({ uploadURL });
    } catch (error) {
      console.error("Error getting upload URL:", error);
      res.status(500).json({ error: "Failed to get upload URL" });
    }
  });

  // Update coach profile image endpoint
  app.put("/api/coaches/:id/profile-image", isAuthenticated, async (req, res) => {
    if (!req.body.imageURL) {
      return res.status(400).json({ error: "imageURL is required" });
    }

    const userId = (req.session as any)?.userId;

    try {
      const coach = await storage.getCoach(req.params.id);
      if (!coach || coach.userId !== userId) {
        return res.status(403).json({ error: "Not authorized to update this coach profile" });
      }

      const objectStorageService = new ObjectStorageService();
      const objectPath = await objectStorageService.trySetObjectEntityAclPolicy(
        req.body.imageURL,
        {
          owner: userId,
          visibility: "public", // Profile images should be public
        },
      );

      // Update the coach's image in the database
      const updatedCoach = await storage.updateCoach(req.params.id, { image: objectPath });

      res.status(200).json({
        objectPath: objectPath,
        coach: updatedCoach
      });
    } catch (error) {
      console.error("Error setting profile image:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Update coach video endpoint
  app.post("/api/coaches/:id/videos", isAuthenticated, async (req, res) => {
    const { title, description, duration, videoURL, thumbnailURL } = req.body;

    if (!title || !description || !duration || !videoURL) {
      return res.status(400).json({ error: "Title, description, duration, and videoURL are required" });
    }

    const userId = (req.session as any)?.userId;

    try {
      const coach = await storage.getCoach(req.params.id);
      if (!coach || coach.userId !== userId) {
        return res.status(403).json({ error: "Not authorized to update this coach profile" });
      }

      const objectStorageService = new ObjectStorageService();

      // Set ACL for video file
      const videoObjectPath = await objectStorageService.trySetObjectEntityAclPolicy(
        videoURL,
        {
          owner: userId,
          visibility: "public", // Instructional videos should be public
        },
      );

      let thumbnailObjectPath = null;
      if (thumbnailURL) {
        thumbnailObjectPath = await objectStorageService.trySetObjectEntityAclPolicy(
          thumbnailURL,
          {
            owner: userId,
            visibility: "public",
          },
        );
      }

      // Create the video in the database
      const video = await storage.createCoachVideo(coach.id, {
        title,
        description,
        duration,
        videoUrl: videoObjectPath,
        thumbnail: thumbnailObjectPath || videoObjectPath, // Use video as thumbnail if no thumbnail provided
      });

      res.status(201).json({
        video,
        videoObjectPath,
        thumbnailObjectPath
      });
    } catch (error) {
      console.error("Error adding coach video:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Normalize profile image URL and set ACL
  app.post("/api/objects/normalize-profile-image", isAuthenticated, async (req, res) => {
    if (!req.body.imageURL) {
      return res.status(400).json({ error: "imageURL is required" });
    }

    const userId = (req.session as any)?.userId;

    try {
      const objectStorageService = new ObjectStorageService();
      const objectPath = await objectStorageService.trySetObjectEntityAclPolicy(
        req.body.imageURL,
        {
          owner: userId,
          visibility: "public", // Profile images should be public
        },
      );

      res.status(200).json({
        objectPath: objectPath,
      });
    } catch (error) {
      console.error("Error normalizing profile image:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Normalize video URL and set ACL
  app.post("/api/objects/normalize-video", isAuthenticated, async (req, res) => {
    if (!req.body.videoURL) {
      return res.status(400).json({ error: "videoURL is required" });
    }

    const userId = (req.session as any)?.userId;

    try {
      const objectStorageService = new ObjectStorageService();
      const objectPath = await objectStorageService.trySetObjectEntityAclPolicy(
        req.body.videoURL,
        {
          owner: userId,
          visibility: "public", // Instructional videos should be public
        },
      );

      res.status(200).json({
        objectPath: objectPath,
      });
    } catch (error) {
      console.error("Error normalizing video:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Normalize thumbnail URL and set ACL
  app.post("/api/objects/normalize-thumbnail", isAuthenticated, async (req, res) => {
    if (!req.body.thumbnailURL) {
      return res.status(400).json({ error: "thumbnailURL is required" });
    }

    const userId = (req.session as any)?.userId;

    try {
      const objectStorageService = new ObjectStorageService();
      const objectPath = await objectStorageService.trySetObjectEntityAclPolicy(
        req.body.thumbnailURL,
        {
          owner: userId,
          visibility: "public", // Thumbnails should be public
        },
      );

      res.status(200).json({
        objectPath: objectPath,
      });
    } catch (error) {
      console.error("Error normalizing thumbnail:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Google Calendar integration routes

  // Get Google Calendar authorization URL
  app.get("/api/calendar/auth-url", isAuthenticated, async (req, res) => {
    try {
      const { GoogleCalendarService } = await import("./googleCalendar");
      const calendarService = new GoogleCalendarService();

      // Generate state parameter for CSRF protection
      const state = Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15);
      (req.session as any).calendarAuthState = state;

      const authUrl = calendarService.getAuthUrl(state);

      res.status(200).json({ authUrl });
    } catch (error) {
      console.error("Error generating auth URL:", error);
      res.status(500).json({ error: "Failed to generate authorization URL" });
    }
  });

  // Handle Google Calendar authorization callback
  app.post("/api/calendar/callback", isAuthenticated, async (req, res) => {
    const { code, coachId, state } = req.body;

    if (!code || !coachId || !state) {
      return res.status(400).json({ error: "Authorization code, coach ID, and state are required" });
    }

    // Verify state parameter to prevent CSRF
    const expectedState = (req.session as any)?.calendarAuthState;
    if (!expectedState || state !== expectedState) {
      return res.status(400).json({ error: "Invalid state parameter" });
    }

    const userId = (req.session as any)?.userId;

    try {
      // Verify the coach belongs to this user
      const coach = await storage.getCoach(coachId);
      if (!coach || coach.userId !== userId) {
        return res.status(403).json({ error: "Not authorized to update this coach profile" });
      }

      const { GoogleCalendarService } = await import("./googleCalendar");
      const calendarService = new GoogleCalendarService();

      // Exchange code for tokens
      const { refreshToken } = await calendarService.exchangeCodeForTokens(code);

      // Set refresh token to get calendar ID
      calendarService.setRefreshToken(refreshToken);
      const calendarId = await calendarService.getPrimaryCalendarId();

      // Store calendar settings
      await storage.updateCoachCalendarSettings(coachId, {
        googleCalendarId: calendarId,
        googleRefreshToken: refreshToken,
        isEnabled: true,
        lastSyncedAt: new Date(),
      });

      // Perform initial sync
      const syncResult = await calendarService.syncExternalEvents(storage, coachId, calendarId);

      res.status(200).json({
        message: "Calendar integration successful",
        syncResult,
      });
    } catch (error) {
      console.error("Error setting up calendar integration:", error);
      res.status(500).json({ error: "Failed to setup calendar integration" });
    }
  });

  // Sync coach calendar manually
  app.post("/api/calendar/sync/:coachId", isAuthenticated, async (req, res) => {
    const { coachId } = req.params;
    const userId = (req.session as any)?.userId;

    try {
      // Verify the coach belongs to this user
      const coach = await storage.getCoach(coachId);
      if (!coach || coach.userId !== userId) {
        return res.status(403).json({ error: "Not authorized to sync this coach's calendar" });
      }

      // Get calendar settings
      const calendarSettings = await storage.getCoachCalendarSettings(coachId);
      if (!calendarSettings?.googleRefreshToken || !calendarSettings?.googleCalendarId) {
        return res.status(400).json({ error: "Calendar integration not set up" });
      }

      const { GoogleCalendarService } = await import("./googleCalendar");
      const calendarService = new GoogleCalendarService();
      calendarService.setRefreshToken(calendarSettings.googleRefreshToken);

      // Perform sync
      const syncResult = await calendarService.syncExternalEvents(
        storage,
        coachId,
        calendarSettings.googleCalendarId,
        calendarSettings.lastSyncToken
      );

      // Update last synced time and sync token
      await storage.updateCoachCalendarSettings(coachId, {
        lastSyncedAt: new Date(),
        lastSyncToken: syncResult.lastSyncToken,
      });

      res.status(200).json({
        message: "Calendar sync completed",
        syncResult,
      });
    } catch (error) {
      console.error("Error syncing calendar:", error);
      res.status(500).json({ error: "Failed to sync calendar" });
    }
  });

  // Get coach availability and busy times
  app.get("/api/coaches/:coachId/availability", async (req, res) => {
    const { coachId } = req.params;
    const { startDate, endDate } = req.query;

    try {
      const coach = await storage.getCoach(coachId);
      if (!coach) {
        return res.status(404).json({ error: "Coach not found" });
      }

      // Get general availability
      const availability = await storage.getCoachAvailability(coachId);

      // Get busy times for date range
      let busyTimes = [];
      if (startDate && endDate) {
        busyTimes = await storage.getCoachBusyTimes(
          coachId,
          new Date(startDate as string),
          new Date(endDate as string)
        );
      }

      res.status(200).json({
        availability,
        busyTimes,
      });
    } catch (error) {
      console.error("Error fetching availability:", error);
      res.status(500).json({ error: "Failed to fetch availability" });
    }
  });

  // Update coach availability
  app.put("/api/coaches/:coachId/availability", isAuthenticated, async (req, res) => {
    const { coachId } = req.params;
    const { availability } = req.body;
    const userId = (req.session as any)?.userId;

    if (!availability || !Array.isArray(availability)) {
      return res.status(400).json({ error: "Valid availability array is required" });
    }

    try {
      // Verify the coach belongs to this user
      const coach = await storage.getCoach(coachId);
      if (!coach || coach.userId !== userId) {
        return res.status(403).json({ error: "Not authorized to update this coach's availability" });
      }

      // Update availability
      await storage.updateCoachAvailability(coachId, availability);

      res.status(200).json({ message: "Availability updated successfully" });
    } catch (error) {
      console.error("Error updating availability:", error);
      res.status(500).json({ error: "Failed to update availability" });
    }
  });

  // Disconnect Google Calendar
  app.delete("/api/calendar/:coachId", isAuthenticated, async (req, res) => {
    const { coachId } = req.params;
    const userId = (req.session as any)?.userId;

    try {
      // Verify the coach belongs to this user
      const coach = await storage.getCoach(coachId);
      if (!coach || coach.userId !== userId) {
        return res.status(403).json({ error: "Not authorized to disconnect this coach's calendar" });
      }

      // Disable calendar integration
      await storage.updateCoachCalendarSettings(coachId, {
        isEnabled: false,
        googleRefreshToken: null,
        googleCalendarId: null,
      });

      res.status(200).json({ message: "Calendar disconnected successfully" });
    } catch (error) {
      console.error("Error disconnecting calendar:", error);
      res.status(500).json({ error: "Failed to disconnect calendar" });
    }
  });

  // Admin routes - protected by isAdmin middleware

  // Check if current user is admin
  app.get("/api/admin/check", isAuthenticated, async (req, res) => {
    const userId = (req.session as any)?.userId;
    const isAdminUser = await storage.isUserAdmin(userId);
    res.status(200).json({ isAdmin: isAdminUser });
  });

  // Get all pending coaches for approval
  app.get("/api/admin/pending-coaches", isAdmin, async (req, res) => {
    try {
      const pendingCoaches = await storage.getPendingCoaches();
      res.status(200).json(pendingCoaches);
    } catch (error) {
      console.error("Error fetching pending coaches:", error);
      res.status(500).json({ error: "Failed to fetch pending coaches" });
    }
  });

  // Approve a coach
  app.post("/api/admin/approve-coach/:coachId", isAdmin, async (req, res) => {
    const { coachId } = req.params;
    const adminId = (req.session as any)?.userId;

    try {
      await storage.approveCoach(coachId, adminId);
      res.status(200).json({ message: "Coach approved successfully" });
    } catch (error) {
      console.error("Error approving coach:", error);
      res.status(500).json({ error: "Failed to approve coach" });
    }
  });

  // Reject a coach
  app.post("/api/admin/reject-coach/:coachId", isAdmin, async (req, res) => {
    const { coachId } = req.params;
    const adminId = (req.session as any)?.userId;

    try {
      await storage.rejectCoach(coachId, adminId);
      res.status(200).json({ message: "Coach rejected successfully" });
    } catch (error) {
      console.error("Error rejecting coach:", error);
      res.status(500).json({ error: "Failed to reject coach" });
    }
  });

  // Get all bookings/lessons
  app.get("/api/admin/bookings", isAdmin, async (req, res) => {
    try {
      const bookings = await storage.getAllBookings();
      res.status(200).json(bookings);
    } catch (error) {
      console.error("Error fetching bookings:", error);
      res.status(500).json({ error: "Failed to fetch bookings" });
    }
  });

  // Get all coaches (approved, pending, rejected)
  app.get("/api/admin/coaches", isAdmin, async (req, res) => {
    try {
      const coaches = await storage.getAllCoaches();
      res.status(200).json(coaches);
    } catch (error) {
      console.error("Error fetching coaches:", error);
      res.status(500).json({ error: "Failed to fetch coaches" });
    }
  });

  // Get all students
  app.get("/api/admin/students", isAdmin, async (req, res) => {
    try {
      const students = await storage.getAllStudents();
      res.status(200).json(students);
    } catch (error) {
      console.error("Error fetching students:", error);
      res.status(500).json({ error: "Failed to fetch students" });
    }
  });

  // Delete a coach
  app.delete("/api/admin/coaches/:coachId", isAdmin, async (req, res) => {
    const { coachId } = req.params;
    const adminId = (req.session as any)?.userId;

    try {
      await storage.deleteCoach(coachId, adminId);
      res.status(200).json({ message: "Coach deleted successfully" });
    } catch (error) {
      console.error("Error deleting coach:", error);
      res.status(500).json({ error: "Failed to delete coach" });
    }
  });

  // Delete a student
  app.delete("/api/admin/students/:studentId", isAdmin, async (req, res) => {
    const { studentId } = req.params;
    const adminId = (req.session as any)?.userId;

    try {
      await storage.deleteStudent(studentId, adminId);
      res.status(200).json({ message: "Student deleted successfully" });
    } catch (error) {
      console.error("Error deleting student:", error);
      res.status(500).json({ error: "Failed to delete student" });
    }
  });

  // Send coach signup link email
  app.post("/api/admin/send-coach-signup", isAdmin, async (req, res) => {
    const { email, message } = req.body;
    const adminId = (req.session as any)?.userId;

    if (!email) {
      return res.status(400).json({ error: "Email is required" });
    }

    try {
      // Check if SendGrid integration exists
      const { EmailService } = await import("./email.js");
      const emailService = new EmailService();

      const signupUrl = `${req.protocol}://${req.get('host')}/coach-registration`;
      const customMessage = message || "You've been invited to join our golf coaching platform as a PGA certified instructor.";

      const emailBody = `
        <h2>Golf Coach Platform Invitation</h2>
        <p>${customMessage}</p>
        <p>Click the link below to register as a coach:</p>
        <p><a href="${signupUrl}" style="background: #007bff; color: white; padding: 12px 24px; text-decoration: none; border-radius: 4px; display: inline-block;">Register as Coach</a></p>
        <p>Or copy and paste this URL: ${signupUrl}</p>
        <p>Best regards,<br>The Golf Coach Team</p>
      `;

      await emailService.sendEmail(
        email,
        "Invitation to Join Golf Coach Platform",
        emailBody
      );

      await storage.logAdminAction(adminId, 'send_signup_link', 'email', undefined, JSON.stringify({ email, message: customMessage }));

      res.status(200).json({ message: "Coach signup email sent successfully" });
    } catch (error) {
      console.error("Error sending signup email:", error);
      res.status(500).json({ error: "Failed to send signup email" });
    }
  });

  // Get admin action logs
  app.get("/api/admin/actions", isAdmin, async (req, res) => {
    const limit = parseInt(req.query.limit as string) || 50;

    try {
      const actions = await storage.getAdminActions(limit);
      res.status(200).json(actions);
    } catch (error) {
      console.error("Error fetching admin actions:", error);
      res.status(500).json({ error: "Failed to fetch admin actions" });
    }
  });

  // Profile Routes
  app.get("/api/students/profile/:userId", isAuthenticated, async (req, res) => {
    try {
      const requestedUserId = req.params.userId;
      const currentUserId = (req.session as any)?.userId;

      // Users can only access their own profile or admins can access any
      const user = await storage.getUser(currentUserId);
      if (currentUserId !== requestedUserId && user?.role !== 'admin') {
        return res.status(403).json({ error: "Not authorized to access this profile" });
      }

      const student = await storage.getStudentByUserId(requestedUserId);
      if (!student) {
        return res.status(404).json({ error: "Student profile not found" });
      }

      res.json({ student });
    } catch (error) {
      console.error("Error fetching student profile:", error);
      res.status(500).json({ error: "Failed to fetch student profile" });
    }
  });

  app.get("/api/coaches/profile/:userId", isAuthenticated, async (req, res) => {
    try {
      const requestedUserId = req.params.userId;
      const currentUserId = (req.session as any)?.userId;

      // Users can only access their own profile or admins can access any
      const user = await storage.getUser(currentUserId);
      if (currentUserId !== requestedUserId && user?.role !== 'admin') {
        return res.status(403).json({ error: "Not authorized to access this profile" });
      }

      // Find coach by user ID
      const coaches = await db.select().from(coaches).where(eq(coaches.userId, requestedUserId));
      const coach = coaches[0];

      if (!coach) {
        return res.status(404).json({ error: "Coach profile not found" });
      }

      res.json({ coach });
    } catch (error) {
      console.error("Error fetching coach profile:", error);
      res.status(500).json({ error: "Failed to fetch coach profile" });
    }
  });

  app.get("/api/bookings/my-bookings", isAuthenticated, async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(401).json({ error: "User not found" });
      }

      let bookings = [];

      if (user.role === 'student') {
        const student = await storage.getStudentByUserId(userId);
        if (student) {
          bookings = await storage.getBookingsByStudent(student.id);
        }
      } else if (user.role === 'coach') {
        const coachList = await db.select().from(coaches).where(eq(coaches.userId, userId));
        const coach = coachList[0];
        if (coach) {
          bookings = await storage.getBookingsByCoach(coach.id);
        }
      }

      // Get coach/student names for each booking
      const enrichedBookings = await Promise.all(bookings.map(async (booking) => {
        const coach = await storage.getCoach(booking.coachId);
        const student = await storage.getStudent(booking.studentId);

        return {
          ...booking,
          coach: coach ? { name: coach.name, image: coach.image } : null,
          student: student ? { name: student.name } : null,
        };
      }));

      res.json({ bookings: enrichedBookings });
    } catch (error) {
      console.error("Error fetching bookings:", error);
      res.status(500).json({ error: "Failed to fetch bookings" });
    }
  });

  // Messaging Routes
  app.get("/api/messages/unread-count", isAuthenticated, async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      const count = await storage.getUnreadMessagesCount(userId);
      res.json({ count });
    } catch (error) {
      console.error("Error fetching unread messages count:", error);
      res.status(500).json({ error: "Failed to fetch unread messages count" });
    }
  });

  app.get("/api/messages/conversations", isAuthenticated, async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      const conversations = await storage.getConversations(userId);
      res.json({ conversations });
    } catch (error) {
      console.error("Error fetching conversations:", error);
      res.status(500).json({ error: "Failed to fetch conversations" });
    }
  });

  app.get("/api/messages/conversation/:otherUserId", isAuthenticated, async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      const otherUserId = req.params.otherUserId;
      const messages = await storage.getMessagesWithUser(userId, otherUserId);
      res.json({ messages });
    } catch (error) {
      console.error("Error fetching conversation:", error);
      res.status(500).json({ error: "Failed to fetch conversation" });
    }
  });

  app.post("/api/messages/mark-read/:senderId", isAuthenticated, async (req, res) => {
    try {
      const userId = (req.session as any)?.userId;
      const senderId = req.params.senderId;
      await storage.markMessagesAsRead(userId, senderId);
      res.json({ message: "Messages marked as read" });
    } catch (error) {
      console.error("Error marking messages as read:", error);
      res.status(500).json({ error: "Failed to mark messages as read" });
    }
  });

  app.post("/api/messages/send", isAuthenticated, async (req, res) => {
    try {
      const senderId = (req.session as any)?.userId;
      const { receiverId, content, bookingId } = req.body;

      if (!receiverId || !content?.trim()) {
        return res.status(400).json({ error: "Receiver ID and message content are required" });
      }

      // Verify receiver exists
      const receiver = await storage.getUser(receiverId);
      if (!receiver) {
        return res.status(404).json({ error: "Receiver not found" });
      }

      const message = await storage.sendMessage({
        senderId,
        receiverId,
        content: content.trim(),
        bookingId: bookingId || null,
      });

      res.json({ message });
    } catch (error) {
      console.error("Error sending message:", error);
      res.status(500).json({ error: "Failed to send message" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
// javascript_database integration - from blueprint:javascript_database
import { 
  users, coaches, students, bookings, reviews, 
  coachSpecialties, coachTools, coachCertifications, coachVideos,
  coachCalendarSettings, coachBusyTimes, coachAvailability, adminActions, messages,
  type User, type InsertUser, type Coach, type Student, 
  type InsertCoach, type InsertStudent, type Booking, type InsertBooking,
  type Review, type InsertReview, type Message, type InsertMessage
} from "@shared/schema";
import { db } from "./db";
import { eq, like, and, desc, sql, type SQL } from "drizzle-orm";
import bcrypt from "bcrypt";

// Interface for user creation with plain password (for internal use)
interface CreateUserData {
  email: string;
  password: string;
  role?: string;
}

// CRUD methods for the golf coach platform
export interface IStorage {
  // User authentication
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: CreateUserData): Promise<User>;
  verifyPassword(email: string, password: string): Promise<User | null>;

  // Coach management
  getCoach(id: string): Promise<Coach | undefined>;
  getCoachWithDetails(id: string): Promise<any>;
  createCoach(coach: InsertCoach): Promise<Coach>;
  updateCoach(id: string, coach: Partial<InsertCoach>): Promise<Coach>;
  updateCoachGoogleReviews(id: string, reviews: {
    googleRating?: string;
    googleReviewCount?: number;
    lastGoogleSync?: Date;
  }): Promise<void>;
  searchCoaches(params: {
    location?: string;
    specialties?: string[];
    priceRange?: { min: number; max: number };
    rating?: number;
    tools?: string[];
  }): Promise<any[]>;

  // Student management
  getStudent(id: string): Promise<Student | undefined>;
  getStudentByUserId(userId: string): Promise<Student | undefined>;
  createStudent(student: InsertStudent): Promise<Student>;
  updateStudent(id: string, student: Partial<InsertStudent>): Promise<Student>;

  // Booking management
  getBooking(id: string): Promise<Booking | undefined>;
  getBookingsByStudent(studentId: string): Promise<Booking[]>;
  getBookingsByCoach(coachId: string): Promise<Booking[]>;
  createBooking(booking: InsertBooking): Promise<Booking>;
  updateBooking(id: string, updates: Partial<Booking>): Promise<Booking>;

  // Review management
  createReview(review: InsertReview): Promise<Review>;
  getReviewsByCoach(coachId: string): Promise<Review[]>;

  // Coach specialties, tools, certifications, and videos
  createCoachSpecialty(coachId: string, specialty: string): Promise<void>;
  createCoachTool(coachId: string, tool: string): Promise<void>;
  createCoachCertification(coachId: string, certification: string): Promise<void>;
  createCoachVideo(coachId: string, video: { title: string; description: string; thumbnail: string; duration: string; videoUrl?: string }): Promise<void>;

  // Calendar and availability management
  getCoachCalendarSettings(coachId: string): Promise<any>;
  updateCoachCalendarSettings(coachId: string, settings: {
    googleCalendarId?: string | null;
    googleRefreshToken?: string | null;
    isEnabled?: boolean;
    lastSyncedAt?: Date;
    lastSyncToken?: string;
  }): Promise<void>;
  updateCoachBusyTime(busyTime: {
    coachId: string;
    startDateTime: Date;
    endDateTime: Date;
    source: string;
    externalEventId: string | null;
    title?: string;
  }): Promise<void>;
  removeCoachBusyTime(coachId: string, externalEventId: string): Promise<void>;
  getCoachBusyTimes(coachId: string, startDate?: Date, endDate?: Date): Promise<any[]>;
  getCoachAvailability(coachId: string): Promise<any[]>;
  updateCoachAvailability(coachId: string, availability: {
    dayOfWeek: number;
    startTime: string;
    endTime: string;
    isAvailable: boolean;
  }[]): Promise<void>;

  // Admin functionality
  isUserAdmin(userId: string): Promise<boolean>;
  getPendingCoaches(): Promise<any[]>;
  approveCoach(coachId: string, adminId: string): Promise<void>;
  rejectCoach(coachId: string, adminId: string): Promise<void>;
  getAllBookings(): Promise<any[]>;
  deleteCoach(coachId: string, adminId: string): Promise<void>;
  deleteStudent(studentId: string, adminId: string): Promise<void>;
  deleteUser(userId: string, adminId: string): Promise<void>;
  logAdminAction(adminId: string, action: string, targetType: string, targetId?: string, details?: string): Promise<void>;
  getAdminActions(limit?: number): Promise<any[]>;

  // Messaging functionality
  sendMessage(message: InsertMessage): Promise<Message>;
  getConversations(userId: string): Promise<any[]>;
  getMessagesWithUser(userId1: string, userId2: string): Promise<Message[]>;
  markMessagesAsRead(userId: string, senderId: string): Promise<void>;
  getUnreadMessagesCount(userId: string): Promise<number>;
}

export class DatabaseStorage implements IStorage {
  // User methods
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async createUser(userData: CreateUserData): Promise<User> {
    // Hash the password before storing
    const saltRounds = 12;
    const hashedPassword = await bcrypt.hash(userData.password, saltRounds);

    // Create the user data for insertion
    const userToInsert = {
      email: userData.email,
      passwordHash: hashedPassword,
      role: userData.role || "student",
    };

    const [user] = await db
      .insert(users)
      .values(userToInsert)
      .returning();
    return user;
  }

  async verifyPassword(email: string, password: string): Promise<User | null> {
    const user = await this.getUserByEmail(email);
    if (!user) {
      return null;
    }

    const isPasswordValid = await bcrypt.compare(password, user.passwordHash);
    return isPasswordValid ? user : null;
  }

  // Coach methods
  async getCoach(id: string): Promise<Coach | undefined> {
    const [coach] = await db.select().from(coaches).where(eq(coaches.id, id));
    return coach || undefined;
  }

  async getCoachWithDetails(id: string): Promise<any> {
    const coach = await db.query.coaches.findFirst({
      where: eq(coaches.id, id),
      with: {
        specialties: true,
        tools: true,
        certifications: true,
        videos: true,
        reviews: {
          with: {
            student: true,
          },
        },
      },
    });
    return coach;
  }

  async createCoach(insertCoach: InsertCoach): Promise<Coach> {
    const [coach] = await db
      .insert(coaches)
      .values(insertCoach)
      .returning();
    return coach;
  }

  async updateCoach(id: string, updates: Partial<InsertCoach>): Promise<Coach> {
    const [coach] = await db
      .update(coaches)
      .set(updates)
      .where(eq(coaches.id, id))
      .returning();
    return coach;
  }

  async updateCoachGoogleReviews(id: string, reviews: {
    googleRating?: string;
    googleReviewCount?: number;
    lastGoogleSync?: Date;
  }): Promise<void> {
    await db
      .update(coaches)
      .set(reviews)
      .where(eq(coaches.id, id));
  }

  async searchCoaches(params: {
    location?: string;
    specialties?: string[];
    priceRange?: { min: number; max: number };
    rating?: number;
    tools?: string[];
  }): Promise<any[]> {
    const conditions = [
      eq(coaches.approvalStatus, 'approved') // Only show approved coaches
    ];

    if (params.location) {
      conditions.push(like(coaches.location, `%${params.location}%`));
    }

    if (params.priceRange) {
      conditions.push(sql`${coaches.pricePerHour} >= ${params.priceRange.min}`);
      conditions.push(sql`${coaches.pricePerHour} <= ${params.priceRange.max}`);
    }

    if (params.rating) {
      conditions.push(sql`${coaches.rating} >= ${params.rating}`);
    }

    const coachResults = await db.query.coaches.findMany({
      where: and(...conditions),
      with: {
        specialties: true,
        tools: true,
        certifications: true,
        videos: true,
      },
      orderBy: [desc(coaches.rating)],
    });

    return coachResults;
  }

  // Student methods
  async getStudent(id: string): Promise<Student | undefined> {
    const [student] = await db.select().from(students).where(eq(students.id, id));
    return student || undefined;
  }

  async getStudentByUserId(userId: string): Promise<Student | undefined> {
    const [student] = await db.select().from(students).where(eq(students.userId, userId));
    return student || undefined;
  }

  async createStudent(insertStudent: InsertStudent): Promise<Student> {
    const [student] = await db
      .insert(students)
      .values(insertStudent)
      .returning();
    return student;
  }

  async updateStudent(id: string, updates: Partial<InsertStudent>): Promise<Student> {
    const [student] = await db
      .update(students)
      .set(updates)
      .where(eq(students.id, id))
      .returning();
    return student;
  }

  // Booking methods
  async getBooking(id: string): Promise<Booking | undefined> {
    const [booking] = await db.select().from(bookings).where(eq(bookings.id, id));
    return booking || undefined;
  }

  async getBookingsByStudent(studentId: string): Promise<Booking[]> {
    return await db.select().from(bookings)
      .where(eq(bookings.studentId, studentId))
      .orderBy(desc(bookings.createdAt));
  }

  async getBookingsByCoach(coachId: string): Promise<Booking[]> {
    return await db.select().from(bookings)
      .where(eq(bookings.coachId, coachId))
      .orderBy(desc(bookings.createdAt));
  }

  async createBooking(insertBooking: InsertBooking): Promise<Booking> {
    const [booking] = await db
      .insert(bookings)
      .values(insertBooking)
      .returning();
    return booking;
  }

  async updateBooking(id: string, updates: Partial<Booking>): Promise<Booking> {
    const [booking] = await db
      .update(bookings)
      .set(updates)
      .where(eq(bookings.id, id))
      .returning();
    return booking;
  }

  // Review methods
  async createReview(insertReview: InsertReview): Promise<Review> {
    const [review] = await db
      .insert(reviews)
      .values(insertReview)
      .returning();

    // Update coach's rating and review count
    const coachReviews = await this.getReviewsByCoach(insertReview.coachId);
    const averageRating = coachReviews.reduce((sum, r) => sum + r.rating, 0) / coachReviews.length;

    await db
      .update(coaches)
      .set({
        rating: averageRating.toFixed(2),
        reviewCount: coachReviews.length,
      })
      .where(eq(coaches.id, insertReview.coachId));

    return review;
  }

  async getReviewsByCoach(coachId: string): Promise<Review[]> {
    return await db.select().from(reviews)
      .where(eq(reviews.coachId, coachId))
      .orderBy(desc(reviews.createdAt));
  }

  // Coach specialties, tools, certifications, and videos methods
  async createCoachSpecialty(coachId: string, specialty: string): Promise<void> {
    await db.insert(coachSpecialties).values({
      coachId,
      specialty,
    });
  }

  async createCoachTool(coachId: string, tool: string): Promise<void> {
    await db.insert(coachTools).values({
      coachId,
      tool,
    });
  }

  async createCoachCertification(coachId: string, certification: string): Promise<void> {
    await db.insert(coachCertifications).values({
      coachId,
      certification,
    });
  }

  async createCoachVideo(coachId: string, video: { title: string; description: string; thumbnail: string; duration: string; videoUrl?: string }): Promise<void> {
    await db.insert(coachVideos).values({
      coachId,
      title: video.title,
      description: video.description,
      thumbnail: video.thumbnail,
      duration: video.duration,
      videoUrl: video.videoUrl || null,
    });
  }

  // Calendar and availability methods
  async getCoachCalendarSettings(coachId: string): Promise<any> {
    const [settings] = await db.select().from(coachCalendarSettings).where(eq(coachCalendarSettings.coachId, coachId));
    return settings || null;
  }

  async updateCoachCalendarSettings(coachId: string, settings: {
    googleCalendarId?: string | null;
    googleRefreshToken?: string | null;
    isEnabled?: boolean;
    lastSyncedAt?: Date;
    lastSyncToken?: string;
  }): Promise<void> {
    const existingSettings = await this.getCoachCalendarSettings(coachId);

    if (existingSettings) {
      await db.update(coachCalendarSettings)
        .set(settings)
        .where(eq(coachCalendarSettings.coachId, coachId));
    } else {
      await db.insert(coachCalendarSettings).values({
        coachId,
        ...settings,
      });
    }
  }

  async updateCoachBusyTime(busyTime: {
    coachId: string;
    startDateTime: Date;
    endDateTime: Date;
    source: string;
    externalEventId: string | null;
    title?: string;
  }): Promise<void> {
    // Check if busy time already exists for this external event
    if (busyTime.externalEventId) {
      const [existingBusyTime] = await db.select()
        .from(coachBusyTimes)
        .where(and(
          eq(coachBusyTimes.coachId, busyTime.coachId),
          eq(coachBusyTimes.externalEventId, busyTime.externalEventId)
        ));

      if (existingBusyTime) {
        // Update existing busy time
        await db.update(coachBusyTimes)
          .set({
            startDateTime: busyTime.startDateTime,
            endDateTime: busyTime.endDateTime,
            title: busyTime.title || 'Busy',
          })
          .where(eq(coachBusyTimes.id, existingBusyTime.id));
        return;
      }
    }

    // Insert new busy time
    await db.insert(coachBusyTimes).values({
      coachId: busyTime.coachId,
      startDateTime: busyTime.startDateTime,
      endDateTime: busyTime.endDateTime,
      source: busyTime.source,
      externalEventId: busyTime.externalEventId,
      title: busyTime.title || 'Busy',
    });
  }

  async removeCoachBusyTime(coachId: string, externalEventId: string): Promise<void> {
    await db.delete(coachBusyTimes)
      .where(and(
        eq(coachBusyTimes.coachId, coachId),
        eq(coachBusyTimes.externalEventId, externalEventId)
      ));
  }

  async getCoachBusyTimes(coachId: string, startDate?: Date, endDate?: Date): Promise<any[]> {
    let query = db.select().from(coachBusyTimes).where(eq(coachBusyTimes.coachId, coachId));

    if (startDate && endDate) {
      query = db.select().from(coachBusyTimes).where(and(
        eq(coachBusyTimes.coachId, coachId),
        sql`${coachBusyTimes.startDateTime} >= ${startDate}`,
        sql`${coachBusyTimes.endDateTime} <= ${endDate}`
      ));
    }

    return await query.orderBy(coachBusyTimes.startDateTime);
  }

  async getCoachAvailability(coachId: string): Promise<any[]> {
    return await db.select().from(coachAvailability)
      .where(eq(coachAvailability.coachId, coachId))
      .orderBy(coachAvailability.dayOfWeek);
  }

  async updateCoachAvailability(coachId: string, availability: {
    dayOfWeek: number;
    startTime: string;
    endTime: string;
    isAvailable: boolean;
  }[]): Promise<void> {
    // Delete existing availability
    await db.delete(coachAvailability).where(eq(coachAvailability.coachId, coachId));

    // Insert new availability
    if (availability.length > 0) {
      await db.insert(coachAvailability).values(
        availability.map(avail => ({
          coachId,
          dayOfWeek: avail.dayOfWeek,
          startTime: avail.startTime,
          endTime: avail.endTime,
          isAvailable: avail.isAvailable,
        }))
      );
    }
  }

  // Admin functionality
  async isUserAdmin(userId: string): Promise<boolean> {
    const user = await db.select().from(users)
      .where(eq(users.id, userId))
      .limit(1);

    return user.length > 0 && user[0].role === 'admin';
  }

  async getPendingCoaches(): Promise<any[]> {
    return await db.select({
      id: coaches.id,
      name: coaches.name,
      email: users.email,
      bio: coaches.bio,
      location: coaches.location,
      pricePerHour: coaches.pricePerHour,
      yearsExperience: coaches.yearsExperience,
      image: coaches.image,
      createdAt: coaches.createdAt,
      approvalStatus: coaches.approvalStatus,
    })
    .from(coaches)
    .innerJoin(users, eq(coaches.userId, users.id))
    .where(eq(coaches.approvalStatus, 'pending'))
    .orderBy(desc(coaches.createdAt));
  }

  async getAllCoaches(): Promise<any[]> {
    return await db.select({
      id: coaches.id,
      name: coaches.name,
      email: users.email,
      bio: coaches.bio,
      location: coaches.location,
      pricePerHour: coaches.pricePerHour,
      yearsExperience: coaches.yearsExperience,
      image: coaches.image,
      createdAt: coaches.createdAt,
      approvalStatus: coaches.approvalStatus,
      userId: coaches.userId,
    })
    .from(coaches)
    .innerJoin(users, eq(coaches.userId, users.id))
    .orderBy(desc(coaches.createdAt));
  }

  async getAllStudents(): Promise<any[]> {
    return await db.select({
      id: students.id,
      name: students.name,
      email: users.email,
      phone: students.phone,
      skillLevel: students.skillLevel,
      createdAt: students.createdAt,
      userId: students.userId,
    })
    .from(students)
    .innerJoin(users, eq(students.userId, users.id))
    .orderBy(desc(students.createdAt));
  }

  async approveCoach(coachId: string, adminId: string): Promise<void> {
    await db.update(coaches)
      .set({
        approvalStatus: 'approved',
        approvedAt: new Date(),
        approvedBy: adminId,
      })
      .where(eq(coaches.id, coachId));

    await this.logAdminAction(adminId, 'approve_coach', 'coach', coachId);
  }

  async rejectCoach(coachId: string, adminId: string): Promise<void> {
    await db.update(coaches)
      .set({
        approvalStatus: 'rejected',
        approvedAt: new Date(),
        approvedBy: adminId,
      })
      .where(eq(coaches.id, coachId));

    await this.logAdminAction(adminId, 'reject_coach', 'coach', coachId);
  }

  async getAllBookings(): Promise<any[]> {
    return await db.select({
      id: bookings.id,
      studentName: students.name,
      studentEmail: users.email,
      coachName: coaches.name,
      date: bookings.date,
      time: bookings.time,
      status: bookings.status,
      totalAmount: bookings.totalAmount,
      createdAt: bookings.createdAt,
    })
    .from(bookings)
    .innerJoin(students, eq(bookings.studentId, students.id))
    .innerJoin(coaches, eq(bookings.coachId, coaches.id))
    .innerJoin(users, eq(students.userId, users.id))
    .orderBy(desc(bookings.createdAt));
  }

  async deleteCoach(coachId: string, adminId: string): Promise<void> {
    // Get coach details first for logging
    const coach = await this.getCoach(coachId);
    if (!coach) throw new Error('Coach not found');

    // Delete coach (cascade will handle related records)
    await db.delete(coaches).where(eq(coaches.id, coachId));

    await this.logAdminAction(adminId, 'delete_coach', 'coach', coachId, JSON.stringify({ name: coach.name }));
  }

  async deleteStudent(studentId: string, adminId: string): Promise<void> {
    // Get student details first for logging
    const student = await this.getStudent(studentId);
    if (!student) throw new Error('Student not found');

    // Delete student (cascade will handle related records)
    await db.delete(students).where(eq(students.id, studentId));

    await this.logAdminAction(adminId, 'delete_student', 'student', studentId, JSON.stringify({ name: student.name }));
  }

  async deleteUser(userId: string, adminId: string): Promise<void> {
    // Get user details first for logging
    const user = await this.getUser(userId);
    if (!user) throw new Error('User not found');

    // Delete user (cascade will handle related records)
    await db.delete(users).where(eq(users.id, userId));

    await this.logAdminAction(adminId, 'delete_user', 'user', userId, JSON.stringify({ email: user.email, role: user.role }));
  }

  async logAdminAction(adminId: string, action: string, targetType: string, targetId?: string, details?: string): Promise<void> {
    await db.insert(adminActions).values({
      adminId,
      action,
      targetType,
      targetId: targetId || null,
      details: details || null,
    });
  }

  async getAdminActions(limit: number = 50): Promise<any[]> {
    return await db.select({
      id: adminActions.id,
      adminEmail: users.email,
      action: adminActions.action,
      targetType: adminActions.targetType,
      targetId: adminActions.targetId,
      details: adminActions.details,
      createdAt: adminActions.createdAt,
    })
    .from(adminActions)
    .innerJoin(users, eq(adminActions.adminId, users.id))
    .orderBy(desc(adminActions.createdAt))
    .limit(limit);
  }

  // Messaging methods
  async sendMessage(message: InsertMessage): Promise<Message> {
    const [newMessage] = await db.insert(messages).values(message).returning();
    return newMessage;
  }

  async getConversations(userId: string): Promise<any[]> {
    // Get all unique users this user has had conversations with
    const conversationsQuery = await db.select({
      userId: sql<string>`CASE 
        WHEN ${messages.senderId} = ${userId} THEN ${messages.receiverId} 
        ELSE ${messages.senderId} 
      END`.as('userId'),
      userName: sql<string>`CASE 
        WHEN ${messages.senderId} = ${userId} THEN receiver_users.email 
        ELSE sender_users.email 
      END`.as('userName'),
      lastMessage: sql<string>`
        (SELECT content FROM ${messages} m2 
         WHERE (m2.senderId = ${userId} AND m2.receiverId = CASE WHEN ${messages.senderId} = ${userId} THEN ${messages.receiverId} ELSE ${messages.senderId} END) 
            OR (m2.receiverId = ${userId} AND m2.senderId = CASE WHEN ${messages.senderId} = ${userId} THEN ${messages.receiverId} ELSE ${messages.senderId} END)
         ORDER BY m2.createdAt DESC LIMIT 1)
      `.as('lastMessage'),
      lastMessageTime: sql<string>`
        (SELECT createdAt FROM ${messages} m2 
         WHERE (m2.senderId = ${userId} AND m2.receiverId = CASE WHEN ${messages.senderId} = ${userId} THEN ${messages.receiverId} ELSE ${messages.senderId} END) 
            OR (m2.receiverId = ${userId} AND m2.senderId = CASE WHEN ${messages.senderId} = ${userId} THEN ${messages.receiverId} ELSE ${messages.senderId} END)
         ORDER BY m2.createdAt DESC LIMIT 1)
      `.as('lastMessageTime'),
      unreadCount: sql<number>`
        (SELECT COUNT(*) FROM ${messages} m3 
         WHERE m3.receiverId = ${userId} 
           AND m3.senderId = CASE WHEN ${messages.senderId} = ${userId} THEN ${messages.receiverId} ELSE ${messages.senderId} END
           AND m3.isRead = false)
      `.as('unreadCount'),
    })
    .from(messages)
    .innerJoin(users, eq(messages.senderId, users.id))
    .innerJoin(users, eq(messages.receiverId, users.id))
    .where(sql`${messages.senderId} = ${userId} OR ${messages.receiverId} = ${userId}`)
    .groupBy(sql`CASE WHEN ${messages.senderId} = ${userId} THEN ${messages.receiverId} ELSE ${messages.senderId} END`)
    .orderBy(desc(sql`lastMessageTime`));

    return conversationsQuery;
  }

  async getMessagesWithUser(userId1: string, userId2: string): Promise<Message[]> {
    return await db.select()
      .from(messages)
      .where(
        sql`(${messages.senderId} = ${userId1} AND ${messages.receiverId} = ${userId2}) 
            OR (${messages.senderId} = ${userId2} AND ${messages.receiverId} = ${userId1})`
      )
      .orderBy(messages.createdAt);
  }

  async markMessagesAsRead(userId: string, senderId: string): Promise<void> {
    await db.update(messages)
      .set({ isRead: true })
      .where(
        and(
          eq(messages.receiverId, userId),
          eq(messages.senderId, senderId)
        )
      );
  }

  async getUnreadMessagesCount(userId: string): Promise<number> {
    const result = await db.select({ count: sql<number>`COUNT(*)` })
      .from(messages)
      .where(
        and(
          eq(messages.receiverId, userId),
          eq(messages.isRead, false)
        )
      );

    return result[0]?.count || 0;
  }
}

export const storage = new DatabaseStorage();

